class letra_guion
{
    public static void main(String args[])
    {
        String palabra ="";
        String palabra2="";
        int    i       =0;
        for (i=0; i < palabra.length(); i++)
        {
            palabra2=palabra2 + palabra.charAt(i);
            if ((i + 1) % 3 == 0)
                palabra2=palabra2 + "-";
        }
        System.out.println(palabra2);
    }
}
